package org.firstinspires.ftc.teamcode;import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;import com.qualcomm.robotcore.eventloop.opmode.TeleOp;import com.qualcomm.robotcore.hardware.DcMotor;import com.qualcomm.robotcore.hardware.DcMotorEx;@TeleOppublic class locomocao extends LinearOpMode {    DcMotorEx FLMotor;    DcMotorEx FRMotor;    DcMotorEx BLMotor;    DcMotorEx BRMotor;    @Override    public void runOpMode() throws InterruptedException {        initialize();        waitForStart();         while (opModeIsActive()){             FLMotor.setPower(gamepad1.left_stick_y);             FRMotor.setPower(gamepad1.left_stick_y);             BLMotor.setPower(gamepad1.left_stick_y);             BRMotor.setPower(gamepad1.left_stick_y);             FLMotor.setPower(gamepad1.left_stick_x);             FRMotor.setPower(-gamepad1.left_stick_x);             BLMotor.setPower(gamepad1.left_stick_x);             BRMotor.setPower(-gamepad1.left_stick_x);             FLMotor.setPower(-gamepad1.right_stick_x);             FRMotor.setPower(gamepad1.right_stick_x);             BLMotor.setPower(gamepad1.right_stick_x);             BRMotor.setPower(-gamepad1.right_stick_x);         }    }    public void initialize(){        FLMotor = hardwareMap.get(DcMotorEx.class, "leftFront");        FRMotor = hardwareMap.get(DcMotorEx.class, "rightFront");        BLMotor = hardwareMap.get(DcMotorEx.class, "leftBack");        BRMotor = hardwareMap.get(DcMotorEx.class, "rightBack");        FLMotor.setDirection(DcMotor.Direction.FORWARD);        FRMotor.setDirection(DcMotor.Direction.REVERSE);        BLMotor.setDirection(DcMotor.Direction.REVERSE);        BRMotor.setDirection(DcMotor.Direction.FORWARD);        FLMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.FLOAT);        FLMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.FLOAT);        FLMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.FLOAT);        FLMotor.setZeroPowerBehavior(DcMotor.ZeroPowerBehavior.FLOAT);    }}